  int main( void )
{

 
  enum key_type key;
  unsigned long old_num=1;
  unsigned long pre_num=1;
  
  
  WDTCTL = WDTPW+WDTHOLD;  // Stop WDT

  LCD_Init();          //显示屏初始化
  LCD_Show_xh();  //初始化在(62,32)处显示数字2020长度为5
  delay_ms(500) ;
  LCD_CLS();
  
  LCD_Show_title();
  delay_ms(500) ;
  LCD_CLS();
  
  LCD_Show_name();
  delay_ms(500) ;
  LCD_CLS();
  
  void IOInit();{   //初始化端口函数

//设置P1.1和P2.1为输入模式  
    P1DIR&=~BIT1;
    P2DIR&=~BIT1;
 //设置P1.1和P2.1使能上拉/下拉模式     
    P1REN|=BIT1;
    P2REN|=BIT1;
//设置P1.1和P2.1为上拉模式      
    P1OUT|=BIT1;
    P2OUT|=BIT1;
//设置P1.0和P4.7为输出模式
    P1DIR|=BIT0;
    P4DIR|=BIT7;
  
  }
  
  IOInit(); //初始化各种端口
  while(1)
  {
    key = KeyScan();
    if( key != NOKEY )
    {
      switch(key)
      {
        case KEYLEFT  : pre_num--;    
        case KEYRIGHT : pre_num++;
      }
    }
      if(old_num!=pre_num)
      {
        LCD_ShowNum(0,0,pre_num,5,16);
      }
      old_num=pre_num;
    }
  }


